import React from "react";
import Image from "./Image"
import './Gallery.css';
import {Row, Button} from 'antd';
import { withRouter } from 'react-router-dom';
import { config } from "../App";
  
class Gallery extends React.Component {
  constructor(props) {
    super(props);
    this.state = { 
      text: "Gallery",
      imagesList: []
    };
  }
  
  async componentDidMount() {
    let response = await fetch(`${config.endpoint}/images`);
    response = await response.json();
    this.setState({
      imagesList: response.images
    });
  }
  
  render() {
    return <div>
            <header>
              <h1 className='header-text'>{this.state.text}</h1>
              <Button className='upload' onClick={()=> this.props.history.push('/new')} type="primary" shape="round" size='large'>
                Upload new image
              </Button>
            </header>
            
            <Row className='gallery-container'>
              {this.state.imagesList.map(image => (
                <Image key={image._id} image={image}/>
              ))}
              
            </Row>
          </div>
  }
}
  
export default withRouter(Gallery);
